import React, { useState, useEffect } from "react";

function KanbanBoard() {
  const [tickets, setTickets] = useState([]);
  const [users, setUsers] = useState([]);
  const [groupBy, setGroupBy] = useState("status"); // default grouping by status
  const [sortBy, setSortBy] = useState("priority"); // default sorting by priority

  useEffect(() => {
    // Simulate an API call with the JSON data
    const jsonResponse = {
      tickets: [
        {
          id: "CAM-1",
          title: "Update User Profile Page UI",
          tag: ["Feature request"],
          userId: "usr-1",
          status: "Todo",
          priority: 4,
        },
        {
          id: "CAM-2",
          title:
            "Add Multi-Language Support - Enable multi-language support within the application.",
          tag: ["Feature Request"],
          userId: "usr-2",
          status: "In progress",
          priority: 3,
        },
        {
          id: "CAM-3",
          title: "Optimize Database Queries for Performance",
          tag: ["Feature Request"],
          userId: "usr-2",
          status: "In progress",
          priority: 1,
        },
        {
          id: "CAM-4",
          title: "Implement Email Notification System",
          tag: ["Feature Request"],
          userId: "usr-1",
          status: "In progress",
          priority: 3,
        },
        {
          id: "CAM-5",
          title: "Enhance Search Functionality",
          tag: ["Feature Request"],
          userId: "usr-5",
          status: "In progress",
          priority: 0,
        },
        {
          id: "CAM-6",
          title: "Third-Party Payment Gateway",
          tag: ["Feature Request"],
          userId: "usr-2",
          status: "Todo",
          priority: 1,
        },
        {
          id: "CAM-7",
          title: "Create Onboarding Tutorial for New Users",
          tag: ["Feature Request"],
          userId: "usr-1",
          status: "Backlog",
          priority: 2,
        },
        {
          id: "CAM-8",
          title: "Implement Role-Based Access Control (RBAC)",
          tag: ["Feature Request"],
          userId: "usr-3",
          status: "In progress",
          priority: 3,
        },
        {
          id: "CAM-9",
          title: "Upgrade Server Infrastructure",
          tag: ["Feature Request"],
          userId: "usr-5",
          status: "Todo",
          priority: 2,
        },
        {
          id: "CAM-10",
          title: "Conduct Security Vulnerability Assessment",
          tag: ["Feature Request"],
          userId: "usr-4",
          status: "Backlog",
          priority: 1,
        },
      ],
      users: [
        { id: "usr-1", name: "Anoop sharma", available: false },
        { id: "usr-2", name: "Yogesh", available: true },
        { id: "usr-3", name: "Shankar Kumar", available: true },
        { id: "usr-4", name: "Ramesh", available: true },
        { id: "usr-5", name: "Suresh", available: true },
      ],
    };

    // Set the tickets and users from the JSON response
    setTickets(jsonResponse.tickets);
    setUsers(jsonResponse.users);
  }, []);

  const groupTickets = () => {
    switch (groupBy) {
      case "status":
        return tickets.reduce((groups, ticket) => {
          const group = groups[ticket.status] || [];
          group.push(ticket);
          groups[ticket.status] = group;
          return groups;
        }, {});
      case "user":
        return tickets.reduce((groups, ticket) => {
          const group = groups[ticket.userId] || [];
          group.push(ticket);
          groups[ticket.userId] = group;
          return groups;
        }, {});
      case "priority":
        return tickets.reduce((groups, ticket) => {
          const group = groups[ticket.priority] || [];
          group.push(ticket);
          groups[ticket.priority] = group;
          return groups;
        }, {});
      default:
        return tickets;
    }
  };

  const sortTickets = (groupedTickets) => {
    return groupedTickets.sort((a, b) => {
      if (sortBy === "priority") {
        return b.priority - a.priority;
      } else if (sortBy === "title") {
        return a.title.localeCompare(b.title);
      } else {
        return 0;
      }
    });
  };

  const renderKanbanBoard = () => {
    const groupedTickets = groupTickets();

    return Object.keys(groupedTickets).map((group) => (
      <div key={group} className="kanban-column">
        <h3>
          {groupBy === "user" ? users.find((u) => u.id === group)?.name : group}
        </h3>
        {sortTickets(groupedTickets[group]).map((ticket) => (
          <div key={ticket.id} className="kanban-card">
            <h4>{ticket.title}</h4>
            <p>{ticket.description}</p>
            <p>Priority: {ticket.priority}</p>
          </div>
        ))}
      </div>
    ));
  };

  return (
    <div>
      <div>
        <label htmlFor="groupBy">Group by: </label>
        <select
          id="groupBy"
          value={groupBy}
          onChange={(e) => setGroupBy(e.target.value)}
        >
          <option value="status">Status</option>
          <option value="user">User</option>
          <option value="priority">Priority</option>
        </select>
      </div>

      <div>
        <label htmlFor="sortBy">Sort by: </label>
        <select
          id="sortBy"
          value={sortBy}
          onChange={(e) => setSortBy(e.target.value)}
        >
          <option value="priority">Priority</option>
          <option value="title">Title</option>
        </select>
      </div>

      <div className="kanban-board">{renderKanbanBoard()}</div>
    </div>
  );
}

export default KanbanBoard;
